mapper.update:
UPDATE travel_information
    SET passed = #{passed}
WHERE travel_id = #{travelId}

mapper.select:
SELECT travel_id, eno, date_format(first_departure_time, '%Y-%m-%d %H:%i:%s') AS firstDepartureTime, date_format(last_arrival_time, '%Y-%m-%d %H:%i:%s') AS lastArrivalTime, days_of_travel, travel_description, date_format(submit_time, '%Y-%m-%d') AS submitTime, subsidy_amount, total_reimbursement, passed FROM travel_information t WHERE eno = #{eno}
AND travel_id = #{travelId}
AND first_departure_time >= #{timeStart}
AND DATE_ADD(#{timeEnd},INTERVAL 1 DAY) > last_arrival_time
AND passed = #{passed}

SELECT t.travel_id AS travelId, t.eno, e.ename, date_format(t.first_departure_time, '%Y-%m-%d %H:%i:%s') AS firstDepartureTime, date_format(t.last_arrival_time, '%Y-%m-%d %H:%i:%s') AS lastArrivalTime, t.days_of_travel AS daysOfTravel, t.travel_description AS travelDescription, date_format(t.submit_time, '%Y-%m-%d') AS submitTime, t.subsidy_amount AS subsidyAmount, t.total_reimbursement AS totalReimbursement, t.passed FROM travel_information t LEFT JOIN employees e ON t.eno = e.eno WHERE t.passed = #{passed}
AND t.travel_id = #{travelId}
AND e.ename LIKE '%' #{ename} '%'
AND t.submit_time >= #{timeStart}
AND DATE_ADD(#{timeEnd},INTERVAL 1 MONTH) > t.submit_time
